$data = json_decode($response, true);
if (json_last_error() !== JSON_ERROR_NONE) {
    die("Invalid JSON: " . json_last_error_msg());
}

// 2) save array for future reference
file_put_contents(__DIR__ . '/data.txt', print_r($data, true));

// 3) prepare time‚Äëseries data for chart: capital_total & profit_brut
$chartData = [];
foreach ($data['bilanturi_mfinante_scurte'] as $key => $yr) {
    if (preg_match('/^an_/', $key) && $yr !== null) {
        $chartData[] = [
            'year'    => $yr['an'],
            'capital' => (float)($yr['capital_total']['valoare'] ?? 0),
            'brut'    => (float)($yr['profit_brut']['valoare']    ?? 0),
            'profit'   => $yr['profit_pierdere_neta']['valoare'] ?? 0,
            'venituri' => $yr['venituri_total']['valoare'] ?? 0,
        ];
    }
}
usort($chartData, fn($a,$b) => $a['year'] <=> $b['year']);

// 4) extract last 10 years of key metrics for AI
$years = [];
foreach ($chartData as $entry) {
    $yearKey = 'an_' . $entry['year'];
    $yr = $data['bilanturi_mfinante_scurte'][$yearKey] ?? null;
    if (!$yr) continue;
    $years[] = [
        'an'       => $yr['an'],
        'profit'   => $yr['profit_pierdere_neta']['valoare'] ?? 0,
        'capital'  => $yr['capital_total']['valoare'] ?? 0,
        'venituri' => $yr['venituri_total']['valoare'] ?? 0,
        'angajati' => $yr['numar_mediu_angajati']['valoare'] ?? 0,
        'brut'     => (float)($yr['profit_brut']['valoare']    ?? 0),
    ];
}
$years = array_slice($years, -10);

// 5) build prompt
$companyName = $data['firma']['nume_recom'] ?? 'Firma';
$prompt  = "AnalizeazƒÉ financiar \"$companyName\" pe baza acestor date (profit, capital, venituri, angaja»õi) pe ultimii 10 ani:\n";
foreach ($years as $y) {
    $prompt .= "An {$y['an']}: Profit={$y['profit']} RON, Capital={$y['capital']} RON, Venituri={$y['venituri']} RON, Profit Brut={$y['brut']}, Angaja»õi={$y['angajati']}\n";
}
$prompt .= "\nScrie un rezumat profesional de 3 paragrafe despre stabilitate, riscuri »ôi direc»õia companiei. Nu folosi bold sau alte forme de formatare.";

// 6) call Azure OpenAI GPT-4o-mini
$apiKey   = '84RhbRJXrltzcl3vJ0Q9XqYuHYn4t7LC3h4ArmXWAgU8mxf1b9dtJQQJ99BEAC5RqLJXJ3w3AAABACOGRNpM';
$endpoint = 'https://logicnetcrm-ai.openai.azure.com/openai/deployments/gpt-4o-mini/chat/completions?api-version=2025-01-01-preview';
$headers  = [
    'Content-Type: application/json',
    'api-key: ' . $apiKey
];
$payload = json_encode([
    'messages'    => [
        ['role'=>'system','content'=>'E»ôti un analist financiar care scrie rezumate inteligente »ôi concise.'],
        ['role'=>'user','content'=>$prompt]
    ],
    'temperature' => 0.7,
    'max_tokens'  => 700
]);
$ch = curl_init($endpoint);
curl_setopt_array($ch, [
    CURLOPT_POST           => true,
    CURLOPT_RETURNTRANSFER => true,
    CURLOPT_HTTPHEADER     => $headers,
    CURLOPT_POSTFIELDS     => $payload,
]);
$aiResponse = curl_exec($ch);
curl_close($ch);
$aiData  = json_decode($aiResponse, true);
$aiReply = $aiData['choices'][0]['message']['content'] ?? 'AI insight unavailable.';
?>

