    const chartData = <?= json_encode($chartData, JSON_NUMERIC_CHECK) ?>;
    const labels    = chartData.map(e=>e.year);
    const capitals  = chartData.map(e=>e.capital);
    const bruts     = chartData.map(e=>e.brut);
    const profit    = chartData.map(e=>e.profit);
    const venituri    = chartData.map(e=>e.venituri);

    new Chart(document.getElementById('capBrutChart'), {
      type: 'line',
      data: {
        labels: labels,
        datasets: [
          { label: 'Capital total (RON)', data: capitals, fill: false, tension: 0.3 },
          { label: 'Profit brut (RON)',    data: bruts,    fill: false, tension: 0.3 },
          { label: 'Profit net (RON)',    data: profit,    fill: false, tension: 0.3 },
          { label: 'Venituri (RON)',    data: venituri,    fill: false, tension: 0.3 }
        ]
      },
      options: {
        scales: { y: { beginAtZero: true, ticks: { callback: v=>new Intl.NumberFormat('ro-RO').format(v) } } },
        plugins: { legend: { display: true } }
      }
    });
  </script>
</body>
</html>
